import './App.css';
import Task from './components/Task'
import TodoInput from './components/TodoInput';
import {useState} from 'react'

function App() {
  let [list, setList] = useState([])

  function addList(newTask){
    setList([...list, newTask])
  }
  return (
    <div className="App">
      <h1>To Do List</h1>
      <TodoInput />
      <Task list={list} />
    </div>
  );
}

export default App;
